mapper.select:
SELECT id,attribcode,attribname FROM M_DIM
WHERE ISACTIVE = 'Y' AND DIMFLAG = #{dimText}
ORDER BY ORDERNO DESC

SELECT '{''yc'':'''||t.yc_value||''',''jk'':'''||t.jk_value||''',''xc'':'''||t.xc_value||'''}' as jsonstr
FROM B_CUSMPTCATEITEM t,B_CUSMPTCATE a,m_product b
WHERE t.b_cusmptcate_id = a.id AND a.m_product_id = b.id AND b.name = #{pdt}
AND t.xw_value = #{xwValue}

SELECT
  t.m_color as colorName
FROM M_COLOR_2 t
WHERE t.m_dim_new14_id = #{mDimNew14Id,jdbcType=DECIMAL}
AND t.M_PRODUCT_ID = #{mProductId,jdbcType=DECIMAL}
and t.isactive = 'Y'
